import { gql } from '@apollo/client';
import { useQuery } from '@apollo/client'

const getBooksQuery = gql`
    {
        books {
            name 
            id
        }
    }
`

function BookList() {
    const data = useQuery(getBooksQuery);
    const displayBooks = () => {
        if(data.loading) {
            return (<div>Data loading</div>);
        }
        else {
            return(data.books.map(book => 
                { <li>{book.name}</li> }
            ))
        }   
    }  
    return (
          <div>
          <ul id="book-list">
            {this.displayBooks()}
          </ul>
      </div>
    );
}
  
export default BookList;
